import './App.css';

class App extends Component {
  constructor(){
    super();
    this.state={
      username:null,
      id:null,
      url:null,
      avatar_url:null,
      followers:null,
      following:null,
      name:null,
      company:null,
      location:null,
      location:null,
      email:null,
      created_at:null,
      updated_at:null
    }
  }
  getUser (username){
    return fetch(`https://api.github.com/users/${username}`)
    .then(response => response.json())
    .then(response => {
      console.log(response);
      return response;
    })
  }
