      username:user.login,
      id:user.id,
      url:user.url,
      avatar_url: user.avatar_url,
      followers:followers,
      following:following,      
      name:name,
      company:company,
      location:location,
      email:email,
      created_at:created_at,
      updated_at:updated_at

    })
  }
  render(){ 
    let user;
    if(this.state.username){
      user=  
      <div>
         <img alt="avatar" src={this.state.avatar_url}/>
        <p>
        {"username:" +this.state.username} <br/>
        {this.state.id}<br/>
        {this.state.url}<br/> 
        {this.state.url}<br/> 
        </p>

      </div>
    }
    return (
      <div className="App">
       <form onSubmit={e => this.handleSubmit(e)}>
         <input ref='username' type='text' placeholder='username'></input>
       </form>
       <p>{user}</p>     
      </div>
    );
  }

}

export default App;
