import React ,{Component} from 'react';
import './App.css';

class App extends Component {
  constructor(){
    super();
    this.state={
      username:null,
      id:null,
      url:null,
      avatar_url:null
    }
  }
  getUser (username){
    return fetch(`https://api.github.com/users/${username}`)
    .then(response => response.json())
    .then(response => {
      console.log(response);
      return response;
    })
  }
 async handleSubmit(e){
    e.preventDefault();
    let user = await this.getUser(this.refs.username.value);
    this.setState({
      username:user.login,
      id:user.id,
      url:user.url,
      avatar: user.avatar_url
    })
  }
  render(){ 
    let user;
    if(this.state.username){
      user=  
      <div>
        <p>{this.state.username} <br/>{this.state.id}<br/>{this.state.url}<br/> </p>
        <img alt="avatar" src={this.state.avatar_url}/>
      </div>
    }
    return (
      <div className="App">
       <form onSubmit={e => this.handleSubmit(e)}>
         <input ref='username' type='text' placeholder='username'></input>
       </form>
       <p>{user}</p>     
      </div>
    );
  }

}

export default App;
