import React, {Component} from "react";

class OrganisationCard extends Component {
    state={
        name:null,
        url:null,
        posts:[]
    };

   async componentDidMount(){
        fetch(`https://api.github.com/organizations?page=1&per_page=140`)
            .then(res=>res.json())
            .then(data=>this.setState({posts:data}))
    }


    
render(){
    let name ;
    if(this.state.name){
        name=  
        <div>
           <img alt="avatar" src={this.state.avatar_url} width='200px'/>
          <p>
          {"username: "+this.state.name} <br/>
          
          </p>
         
        </div>
      };

    return(
        <div className="col-md-3">
            <div className="card">
            <div class="card-header">
            <p>{name}</p>
  
  </div>
  
            </div>
        </div>)
    }

}



export default OrganisationCard;
