import React, {Component} from "react";


class OrganisationCard extends Component {    
        
        state={
          name:null,   
          url:null,  
          login:null, 
          avatar_url:null,   
          key: null   
        }
      

   componentDidMount(){
   const {name, url, key} = this.props;   
   const avatar_url=`https://avatars2.githubusercontent.com/u/${key}?v=4`;
   this.setState({
    name,
    avatar_url,
    url,
    key
   })
   }
    
render(){
    let img;
    if(this.state.username){
      user=  
      <div>
         <img alt="avatar" src={this.state.avatar_url} width='200px'/>
        <p>
        {"username: "+this.state.username} <br/>
        {"id: "+this.state.id}<br/>
        {"followers: "+this.state.followers}<br/> 
        {"following: "+this.state.following}<br/>        
        {"created_at: "+this.state.created_at}<br/> 
        {"updated_at: "+this.state.updated_at}<br/> 
        </p>
       
      </div>
    };
    return(
        <div className="col-md-3">
            <div className="card">
                <div className="card-header">
                    <p className="name">{this.state.name}</p> 
                    <p>{this.state.key}</p>                  
                   <img src={this.state.avatar_url} width="50px"></img>
                </div>  
            </div>
        </div>)
    }

}



export default OrganisationCard;
