    e.preventDefault();
    let user = await this.getUser(this.refs.username.value);
    this.setState({
      username:user.login,
      id:user.id,
      url:user.url,
      avatar_url: user.avatar_url,
      followers:user.followers,
      following:user.following,      
      name:user.name,
      company:user.company,
      location:user.location,
      email:user.email,
      created_at:user.created_at,
      updated_at:user.updated_at

    })
  }
  render(){ 
    let user;
    if(this.state.username){
      user=  
      <div>
         <img alt="avatar" src={this.state.avatar_url}/>
        <p>
        {"username"+this.state.username} <br/>
        {"id"+this.state.id}<br/>
        {"image url"+this.state.url}<br/> 
        {"followers"+this.state.followers}<br/> 
        {"username"+this.state.following}<br/> 
        {"username"+this.state.name}<br/> 
        {"username"+this.state.company}<br/> 
        {"username"+this.state.location}<br/> 
        {"username"+this.state.email}<br/> 
        {"username"+this.state.follocreated_atwers}<br/> 
        {"username"+this.state.updated_at}<br/> 
        </p>

      </div>
    }
    return (
      <div className="App">
       <form onSubmit={e => this.handleSubmit(e)}>
         <input ref='username' type='text' placeholder='username'></input>
       </form>
       <p>{user}</p>     
      </div>
    );
  }

}

export default App;
