      updated_at:user.updated_at

    })
  }
  render(){ 
    let user;
    if(this.state.username){
      user=  
      <div>
         <img alt="avatar" src={this.state.avatar_url} width='200px'/>
        <p>
        {"username: "+this.state.username} <br/>
