import React, {Component} from "react";

class OrganisationCard extends Component {
    state={
        name:null,
        avatar_url:`https://avatars2.githubusercontent.com/u/44?v=4`,            
    };

   componentDidMount(){
   const {name, avatar_url} = this.props;
   this.setState({
    name,
    avatar_url
   })
   }
    
render(){

    return(
        <div className="col-md-3">
            <div className="card">
            <div className="card-header">
            <p className="name">{this.state.name}</p>
            <img alt="avatar" src={this.state.avatar_url} width='50px'/>
  
  </div>
  
            </div>
        </div>)
    }

}



export default OrganisationCard;
