import React, {Component} from "react";
import axios from "axios";

import OrganisationCard from "./OrganisationCard";


class OrganisationList extends Component {
    state={
        url:`https://api.github.com/organizations?page=1&per_page=140`,
        organisation:[],
        avatar_url:null,
        id:null,       
        avatar_url:null,
        description:null,
         name:null,
         location:null,
        email:null,
        public_repos:null,
        followers:null,
        following:null,
        created_at:null,
        updated_at:null,
        members_url:null

    };

   async componentDidMount(){
       const res=await axios.get(this.state.url);    
       this.setState({organisation:res.data});
       members_url(` https://api.github.com/orgs/${this.props.name}/members`)
        .then(res=>res.json())
        .then(data=>this.setState({posts:data}))
      
    }
render(){
    return(
      <React.Fragment>
          {this.state.organisation?(
              <div className="row">
                  {this.state.organisation.map(organisation=>(
                      <OrganisationCard 
                      key={organisation.id}
                      name={organisation.login}
                      url={organisation.avatar_url}
                      description={organisation.description}
                      location={organisation.location}
                      email={organisation.email}
                      followers={organisation.followers}
                      members_url={organisation.members_url}
                      
                      />
                  ))}
              </div>
          ):(
              <h1>Loading ...</h1>
          )}
      </React.Fragment>
        )
    }

}



export default OrganisationList;
