import React, {Component} from "react";
import styled from "styled-components";


class OrganisationCard extends Component {
    constructor(){
        super();
        this.state={
          name:null,   
          url:`https://api.github.com/organizations`,   
          avatar_url:null,         
        }
      }

   componentDidMount(){
   const {name, avatar_url} = this.props;
   this.setState({
    name,
    avatar_url
   })
   }
    
render(){

    return(
        <div className="col-md-3">
            <div className="card">
            <div className="card-header">
            <p className="name">{this.state.name}</p>
            <img src={this.state.avatar_url}></img>
  
            </div>
  
            </div>
        </div>)
    }

}



export default OrganisationCard;
