import React, {Component} from "react";
import styled from "styled-components";


class OrganisationCard extends Component {    
        
        state={
          name:null,   
          url:`https://api.github.com/organizations`,  
          id:null, 
          avatar_url:`https://avatars2.githubusercontent.com/u/${id}?v=4`,         
        }
      

   componentDidMount(){
   const {name, avatar_url, url, id} = this.props;
   this.setState({
    name,
    avatar_url,
    url,
    id
   })
   }
    
render(){

    return(
        <div className="col-md-3">
            <div className="card">
                <div className="card-header">
                    <p className="name">{this.state.name}</p>                   
                   <img src="https://avatars2.githubusercontent.com/u/${id}?v=4" width="50px"></img>
                </div>  
            </div>
        </div>)
    }

}



export default OrganisationCard;
