import React, {Component} from "react";
import styled from "styled-components";


class OrganisationCard extends Component {    
        
        state={
          name:null,   
          url:null,  
          login:null, 
          avatar_url:null,   
          index: ' '      
        }
      

   componentDidMount(){
   const {name, url, login} = this.props;
   const index=url.split('/')[url.split('/').length];
   const avatar_url=`https://avatars2.githubusercontent.com/u/${index}?v=4`;
   this.setState({
    name,
    avatar_url,
    url,
    login
   })
   }
    
render(){

    return(
        <div className="col-md-3">
            <div className="card">
                <div className="card-header">
                    <p className="name">{this.state.name}</p>                   
                   <img src={this.state.avatar_url} width="50px"></img>
                </div>  
            </div>
        </div>)
    }

}



export default OrganisationCard;
