import './App.css';

class App extends Component {
  constructor(){
    super();
    this.state={
      username:null,
      id:null,
      url:null,
      avatar_url:null
    }
  }
  getUser (username){
    return fetch(`https://api.github.com/users/${username}`)
    .then(response => response.json())
    .then(response => {
      console.log(response);
      return response;
    })
  }
